import "nolang/os";

import {parseInline} from "./inline";

async function main() {
  console.log(parseInline("abc `code` def"));
  console.log(parseInline("abc _foobar_ def"));
  console.log(parseInline("abc *foobar* def"));
  console.log(parseInline("abc __foobar__ def"));
  console.log(parseInline("abc **foobar** def"));

  console.log(parseInline("abc [hi google](http://google.com \"The Google\") def"));
  console.log(parseInline("abc ![hi google](http://foo.bar/cat.jpg \"cat image\") def"));
  console.log(parseInline("abc [hi reference][reference] def"));

  console.log(parseInline("abc\n def\nghi"));

  console.log(parseInline("abc <foo a='a' b='b'>hi</foo> <bar/> def"));

}

main();

